import java.util.*;
public class method_loa {

	/**
	 * @param args
	 */
    	int add(int a,int b)
		{
			int c;
			c=a+b;
			return c;
		}
		double add(double a,double b)
		{
			double c;
			c=a+b;
			return c;
		}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			method_loa d=new method_loa();
			System.out.println("The Sum of  integers is="+d.add(4,10));
			System.out.println("The Sum of  float  is="+d.add(4.5,5.2));
		}
		}

	
